package com.example.pluginmusicplayer;import android.content.Context;import android.text.TextUtils;import android.util.AttributeSet;import android.view.LayoutInflater;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.TextView;public class TrackView extends LinearLayout {	protected int mPlayMode;	private Context mContext;	private TextView mTitle, mSubTitle;	private ImageView mIcon;	public TrackView(Context context, AttributeSet attrs) {		super(context, attrs);		init(context);	}	public TrackView(Context context) {		super(context);		init(context);	}	private void init(Context context) {		mContext = context;		LayoutInflater.from(context).inflate(R.layout.music_track_item, this);		mTitle = (TextView) findViewById(R.id.track_title);		mSubTitle = (TextView) findViewById(R.id.artist_nm);		mIcon = (ImageView) findViewById(R.id.track_icon);	}	private boolean mIsAdult, mIsListenable;	public void setTrackState(boolean isPlaying, String isListenable, String isSaved, String isAdult, String data, int restrict) {		boolean isLocal = !TextUtils.isEmpty(data);		mIsAdult = Boolean.parseBoolean(isAdult);		mIsListenable = Boolean.parseBoolean(isListenable);		if (isPlaying) {			mIcon.setImageResource(R.drawable.icon_track_play);		} else {			if (mIsAdult) {				mIcon.setImageResource(R.drawable.icon_track_19);			} else if (Boolean.parseBoolean(isSaved)) {				mIcon.setImageResource(R.drawable.icon_track_save);			} else if (isLocal) {				mIcon.setImageResource(R.drawable.icon_track_local);			} else {			}			if (mIsListenable || isLocal) {				setClickable(false);				setFocusable(false);			} else {			}		}	}	public boolean isAdult() {		return mIsAdult;	}}